int 
main(void)
{
	float nota[5], media;
	int cont;

	media=0.0;
	cont=0;

		while(cont>=999){ 
			scanf("%f",nota[cont]); 
			media=media+nota[cont]); 
			cont++; 
		}  
			for(cont=0; cont<=4; cont++){ 
				if(nota[cont]>media)
					printf("a nota %d eh maior que a media da sala",cont+1);
				else
					printf("a nota %d eh menor que a media da sala",cont+1);
			}
	return 0;
}
