	scanf("%f",&nota2);
	scanf("%f",&nota3);
	scanf("%f",&nota4);
	scanf("%f",&nota5);
	media=nota1+nota2+nota3+nota4+nota5;
	if(nota1>media)
		printf("%f > %f",nota1,media);
	if(nota2>media)
